mapper.select:
select count(*) from ssm_question a WHERE a.flag =1   
and a.categoryid = #{category_pid}
and a.subjectid = #{category_id}

 and a.chapterid = #{chapter_id}

and a.kind =#{kind}
and a.type = #{type}

SELECT A.questionid questionid, A.title title, B.title categoryPidTitle, C.title categoryidTitle, D.title chapterTitle, A.type type , A.status status, A.addtime addtime , A.browse browse , A.kind kind, A.sort_no sort_no FROM ssm_question AS A LEFT JOIN ssm_category B ON A.categoryid = B.id LEFT JOIN ssm_category AS C ON A.subjectid = C.id LEFT JOIN ssm_question_chapter as D ON A.chapterid =D.chapter_id WHERE A.flag = 1
and A.categoryid = #{category_pid}
and A.subjectid = #{category_id}
and A.chapterid = #{chapter_id}
and A.kind = #{kind}
and A.title LIKE CONCAT('%',#{title},'%')
ORDER BY A.sort_no limit #{start},#{end}

SELECT a.questionid questionid, a.title title, a.type type, a.correctanswer correctanswer, b.no no , b.title optionTitle, b.sort_no sortNo, IFNULL(c.myanser,"") myanser FROM ssm_question a LEFT JOIN ssm_question_option b ON a.questionid = b.questionid LEFT JOIN (select questionid,myanser from ssm_question_study where userid=#{userid}) AS c ON a.questionid = c.questionid WHERE a.flag = 1 and a.kind = 2 and a.categoryid =#{category_pid} and a.subjectid= #{category_id}
and a.chapterid= #{chapter_id}
ORDER BY a.sort_no,b.sort_no LIMIT #{start},#{end}
